$x0 = file_get_html($url);
$fold = $mm[0];
$str = $mm[1];

$str = $x0->find('.exercisemenuinner', 0)->innertext;
$fold = explode('/', explode('w3schools.com/', $url)[1])[0];

//echo "<br/>\n  $fold";
//return;

$xx = str_get_html($str);

$bai = 0;
foreach ($xx->find('.exmenuitemheader') as $d) {
    $less = trim(strip_tags($d->innertext));
    $bai++;
    echo "<br/>\n $less";
    $next = $d->next_sibling();

    $bai0 = 0;
    foreach ($next->find('a') as $a1) {
        if ($a1->target == '_blank') {
            continue;
        }

        $bai0++;
        $link = "https://www.w3schools.com/$fold/$a1->href";
        $exec = trim(strip_tags($a1->innertext));
        echo "<br/>\n - $exec | $link  ";
        //        echo "<br/>\n <a href='$link'> LINK </a>";

        $b00 = sprintf('%02d', $bai);
        $name = $b00.'.'.$bai0.'-'.$less."-$exec";
        $name = str_replace(' ', '_', $name);

        $foldStore = "e:/5-q-a-w3school/all-q-a-$fold";
        if (! file_exists($foldStore)) {
            mkdir($foldStore, 0755, 1);
        }

        $fileQ = "$foldStore/$name-q.png";
        $fileQTrim = "$foldStore/$name-q-trim.png";
        $fileA = "$foldStore/$name-a.png";
        $fileATrim = "$foldStore/$name-a-trim.png";

        try {
            if (! file_exists($fileQ)) {

                saveLinkToImg($link, $fileQ, $fileA);

                //Trim các space trắng
                $img = imagecreatefrompng($fileQ);
                $cropped = imagecropauto($img, IMG_CROP_WHITE);
                imagepng($cropped, $fileQTrim);

                //Trim các space trắng
                $img = imagecreatefrompng($fileA);
                $cropped = imagecropauto($img, IMG_CROP_WHITE);
                imagepng($cropped, $fileATrim);

            }
        } catch (\Throwable $e) { // For PHP 7
            echo "<br/>\n Error1: ".$e->getMessage();
        } catch (\Facebook\WebDriver\Exception\WebDriverCurlException $exception) {
            echo "<br/>\n Error2: ".$exception->getMessage();
        }
    }
